    __test__ = False

    def setUp(self) -> None:
        super().setUp()
        self.genesis_block_hash = self._settings.GENESIS_BLOCK_HASH.hex()
        self.genesis_tx1_hash = self._settings.GENESIS_TX1_HASH.hex()
        self.genesis_tx2_hash = self._settings.GENESIS_TX2_HASH.hex()

        self.genesis_block_script = base64.b64encode(self._settings.GENESIS_OUTPUT_SCRIPT).decode('utf-8')
        self.genesis_block_address = not_none(P2PKH.parse_script(self._settings.GENESIS_OUTPUT_SCRIPT)).address

